public class day1 {
    public static void main(String[] args) {
        String sequence = "";
        //Part One
        System.out.println(sequence.length());
        int sum = 0;
        int current;
        int next;
        int first = Character.getNumericValue(sequence.charAt(0));
        int last = Character.getNumericValue(sequence.charAt(sequence.length() - 1));
        for(int i = 0; i < sequence.length() - 1; i++) {
            current = Character.getNumericValue(sequence.charAt(i));
            next = Character.getNumericValue(sequence.charAt(i + 1));
            if(current == next) {
                sum += current;
            }
        }
        if(first == last) {
            sum += first;
        }
        System.out.println(sum);
        //Part Two
        int sum2 = 0;
        int current2;
        int next2;
        int halfLength = sequence.length() / 2;
        for(int i = 0; i < halfLength; i++) {
            current2 = Character.getNumericValue(sequence.charAt(i));
            System.out.println("At value at index " + i + " we are comparing it to the value at index " + (i + halfLength));
            next2 = Character.getNumericValue(sequence.charAt(i + halfLength));
            if(current2 == next2) {
                sum2 += current2;
            }
        }
        //This is because the loop goes halfway through and if it went one further, it would be doing the first comparison that was performed, reversed.
        //Since it's repeating all the comparisons again, you can just double the sum from the first half.
        sum2 = sum2 * 2;
        System.out.println(sum2);
    }
}
